{
    frameCountInteraction1Interval++;
    if(frameCountInteraction1Interval == TICK_INTERACTION1_INTERVAL)
    {
        //Interaction1
        //When every N tick call virtual function
        //Call tickEventScreen
        tickEventScreen();
        frameCountInteraction1Interval = 0;
    }
}
